import React, { useState, useEffect, useCallback } from 'react';
// PERBAIKAN IMPORT API: Impor fungsi yang diekspor secara individual
import { updateOrderStatus, submitReview, fetchOrders, verifyPayment } from '../services/api'; 
import OrderCard from '../components/OrderCard';
import ReviewModal from '../components/ReviewModal'; 
import PaymentProofModal from '../components/PaymentProofModal';

// *** SOLUSI SEMENTARA UNTUK MENGATASI ERROR AuthContext ***
// Ganti dengan import { useAuth } dari file context Anda yang sebenarnya saat sudah dibuat
const useAuth = () => ({
    user: { role: 'customer' } // Default ke customer agar tombol konfirmasi muncul
});
// ***********************************************************************************


const OrdersPage = () => {
    // INISIALISASI STATE `orders` SEBAGAI ARRAY KOSONG AGAR .length TIDAK UNDEFINED
    const [orders, setOrders] = useState([]); 
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    
    // STATE UNTUK REVIEW MODAL
    const [isReviewModalOpen, setIsReviewModalOpen] = useState(false);
    const [itemToReview, setItemToReview] = useState(null);
    const [reviewOrderId, setReviewOrderId] = useState(null);
    
    // STATE UNTUK PAYMENT PROOF MODAL
    const [isProofModalOpen, setIsProofModalOpen] = useState(false);
    const [currentPaymentProof, setCurrentPaymentProof] = useState(null);

    const { user } = useAuth(); 
    const isOwner = user?.role === 'owner';

    // Mendapatkan data pesanan (useCallback agar fungsi stabil)
    const fetchOrdersCallback = useCallback(async () => {
        try {
            setLoading(true);
            const data = await fetchOrders(isOwner); 
            setOrders(data);
            setError(null);
        } catch (err) {
            setError('Failed to fetch orders.');
            console.error(err);
        } finally {
            setLoading(false);
        }
    }, [isOwner]);

    useEffect(() => {
        fetchOrdersCallback();
    }, [fetchOrdersCallback]);
    
    // Fungsi untuk memperbarui status (digunakan Owner)
    const handleUpdateStatus = async (orderId, newStatus) => {
        try {
            await updateOrderStatus(orderId, newStatus); 
            fetchOrdersCallback();
        } catch (err) {
            setError(`Failed to update status: ${err.message}`);
        }
    };

    // Fungsi untuk verifikasi pembayaran (digunakan Owner)
    const handleVerifyPayment = async (orderId, verificationStatus) => {
        try {
            await verifyPayment(orderId, verificationStatus); 
            fetchOrdersCallback();
        } catch (err) {
            setError(`Failed to verify payment: ${err.message}`);
        }
    };
    
    // *** FUNGSI KONFIRMASI PENGIRIMAN (Digunakan Customer) ***
    const handleConfirmDelivery = async (orderId) => {
        try {
            // 1. Update status pesanan ke 'completed'
            await updateOrderStatus(orderId, 'completed'); 
            
            // 2. Refresh daftar pesanan (Penting: agar OrderCard merender status baru)
            // Ini akan memicu OrderCard merender tombol "Tulis Review"
            await fetchOrdersCallback(); 
            
            // 3. Cari pesanan yang baru saja di-complete untuk memicu review
            // Mengambil order dari state lama karena fetchOrdersCallback() bersifat async dan setOrders belum selesai
            const completedOrder = orders.find(o => o.id === orderId); 
            
            if (completedOrder && completedOrder.items.length > 0) {
                // Memicu modal review untuk item pertama di pesanan
                handleReviewItem(orderId, completedOrder.items[0]); 
            } else {
                 alert('Pesanan telah diterima. Terima kasih!');
            }
        } catch (err) {
            setError(`Failed to confirm delivery: ${err.message}`);
            console.error(err);
        }
    };
    // ***************************************************

    // Fungsi untuk membuka modal review
    const handleReviewItem = (orderId, item) => {
        setReviewOrderId(orderId);
        setItemToReview(item);
        setIsReviewModalOpen(true);
    };

    // Fungsi untuk menutup modal review
    const handleCloseReviewModal = () => {
        setIsReviewModalOpen(false);
        setItemToReview(null);
        setReviewOrderId(null);
    };

    // Fungsi untuk mengirimkan review
    const handleSubmitReview = async ({ rating, comment }) => {
        if (!itemToReview || !reviewOrderId) return;

        const reviewData = {
            orderId: reviewOrderId,
            menuItemId: itemToReview.id,
            rating: rating,
            comment: comment,
        };
        
        try {
            await submitReview(reviewData); 
            alert('Review submitted successfully!');
            handleCloseReviewModal();
            fetchOrdersCallback(); // Refresh orders setelah review
        } catch (err) {
            setError(`Failed to submit review: ${err.message}`);
            console.error(err);
        }
    };
    
    // Fungsi untuk menampilkan bukti pembayaran
    const handleViewPaymentProof = (proofUrl) => {
        setCurrentPaymentProof(proofUrl);
        setIsProofModalOpen(true);
    };


    if (loading) return <div className="p-6 text-center text-gray-500">Loading orders...</div>;
    if (error) return <div className="p-6 text-center text-red-500 font-semibold">{error}</div>;


    return (
        <div className="container mx-auto p-4 md:p-8">
            <h1 className="text-3xl font-bold mb-6 text-gray-800">
                {isOwner ? 'Manage Orders' : 'Your Orders'}
            </h1>
            
            <div className="space-y-6">
                {/* SAFE CHECK: Memastikan orders adalah array sebelum memanggil .length */}
                {orders && orders.length > 0 ? ( 
                    orders.map(order => (
                        <OrderCard
                            key={order.id}
                            order={order}
                            isOwner={isOwner}
                            onUpdateStatus={handleUpdateStatus}
                            onReviewItem={handleReviewItem}
                            onConfirmDelivery={handleConfirmDelivery} 
                            onViewPaymentProof={handleViewPaymentProof}
                            onVerifyPayment={handleVerifyPayment}
                            reviews={order.reviews || []}
                        />
                    ))
                ) : (
                    <p className="text-center text-gray-500">No orders found.</p>
                )}
            </div>

            {/* Review Modal */}
            {isReviewModalOpen && itemToReview && (
                <ReviewModal 
                    isOpen={isReviewModalOpen}
                    onClose={handleCloseReviewModal}
                    onSubmit={handleSubmitReview}
                    itemName={itemToReview.name}
                />
            )}
            
            {/* Payment Proof Modal */}
            {isProofModalOpen && (
                <PaymentProofModal
                    isOpen={isProofModalOpen}
                    onClose={() => setIsProofModalOpen(false)}
                    paymentProofUrl={currentPaymentProof}
                />
            )}
        </div>
    );
};

export default OrdersPage;
