    ownerId: 2 
  },
];

export const categories = ['All', 'Main Course', 'Drinks', 'Snacks', 'Desserts'];

// Helper functions
export const formatCurrency = (amount) => {
  return `Rp ${amount.toLocaleString('id-ID')}`;
};

// Authentication API
export const loginUser = async (email, password, role) => {
  // Mock login - replace with actual API call
  return {
    id: 1,
    name: email.split('@')[0],
    email: email,
    role: role
  };
};

export const registerUser = async (name, email, password, role) => {
  // Mock register - replace with actual API call
  return {
    id: Date.now(),
    name: name,
    email: email,
    role: role
  };
};

// Review API
export const getMenuReviews = async (menuItemId) => {
  // Mock get reviews - replace with actual API call
  return [];
};

export const calculateAverageRating = (reviews) => {
  if (!reviews || reviews.length === 0) return 0;
  const sum = reviews.reduce((acc, review) => acc + review.rating, 0);
  return (sum / reviews.length).toFixed(1);
};

// Payment methods
export const paymentMethods = [
  {
    id: 'qris',
    name: 'QRIS',
    type: 'qr',
    icon: '導',
    description: 'Scan QR Code dengan aplikasi e-wallet'
  },
  {
    id: 'dana',
    name: 'DANA',
    type: 'ewallet',
    icon: '諜',
    account: '081234567890',
    accountName: 'FoodHub Restaurant'
  },
  {
    id: 'gopay',
    name: 'GoPay',
    type: 'ewallet',
    icon: '丁',
    account: '081234567890',
    accountName: 'FoodHub Restaurant'
  },
  {
    id: 'ovo',
    name: 'OVO',
    type: 'ewallet',
    icon: '凋',
    account: '081234567890',
    accountName: 'FoodHub Restaurant'
  },
  {
    id: 'bca',
    name: 'Bank BCA',
    type: 'bank',
    icon: '嘗',
    account: '1234567890',
    accountName: 'FoodHub Restaurant'
  },
  {
    id: 'mandiri',
    name: 'Bank Mandiri',
    type: 'bank',
    icon: '嘗',
    account: '9876543210',
    accountName: 'FoodHub Restaurant'
  },
  {
    id: 'bri',
    name: 'Bank BRI',
    type: 'bank',
    icon: '嘗',
    account: '5555666677778888',
    accountName: 'FoodHub Restaurant'
  }
];

// Payment status types
export const paymentStatus = {
  WAITING_PAYMENT: 'waiting_payment',
  PAYMENT_UPLOADED: 'payment_uploaded',
  PAYMENT_CONFIRMED: 'payment_confirmed',
  PAYMENT_REJECTED: 'payment_rejected'
};

// Order status with payment
export const orderStatus = {
  WAITING_PAYMENT: 'waiting_payment',
  PAYMENT_UPLOADED: 'payment_uploaded',
  PENDING: 'pending',
  PROCESSING: 'processing',
  READY: 'ready',
  DELIVERED: 'delivered',
  COMPLETED: 'completed',
  CANCELLED: 'cancelled'
};

// --- MOCK STATE PESANAN LOKAL ---
let mockOrders = []; 
let nextOrderId = 104; 


// MOCK: Mengambil daftar pesanan
export const fetchOrders = async (isOwner = false) => {
    console.log(`Mock: Fetching orders. Current total: ${mockOrders.length}`);
    return [...mockOrders];
};

// MOCK: Verifikasi Pembayaran
export const verifyPayment = async (orderId, verificationStatus) => {
    const orderIndex = mockOrders.findIndex(o => o.id === orderId);
    if (orderIndex !== -1) {
        mockOrders[orderIndex].status = verificationStatus === 'approved' ? 'pending' : 'cancelled';
        mockOrders[orderIndex].paymentStatus = verificationStatus;
    }
    return { orderId, status: mockOrders[orderIndex]?.status || 'unknown' };
};

// MOCK: Place Order
export const placeOrder = async (orderData) => {
    const newOrder = {
        id: nextOrderId++,
        date: new Date().toLocaleDateString(),
        customerName: 'Current User', 
        total: orderData.total,
        status: 'waiting_payment',
        paymentMethod: orderData.paymentMethod,
        items: orderData.items.map(item => ({
             ...item, 
             name: initialMenuItems.find(m => m.id === item.id)?.name || 'Unknown Item'
        })),
        reviews: [] // Array untuk review tingkat pesanan
    };
    mockOrders.push(newOrder);
    console.log(`Mock: Order placed. New total: ${mockOrders.length}`);
    return newOrder;
};

// FUNGSI UNTUK UPDATE STATUS PESANAN (termasuk ke 'completed')
export const updateOrderStatus = async (orderId, newStatus) => {
  const orderIndex = mockOrders.findIndex(o => o.id === orderId);
  if (orderIndex !== -1) {
    mockOrders[orderIndex].status = newStatus;
    
    // Simulasikan Owner menerima pesanan dan mengirimkannya (untuk pengujian alur)
    if (newStatus === 'pending') {
        setTimeout(() => {
            console.log(`[Mock Timer]: Order ${orderId} automatically moved to Delivered for testing.`);
            const deliveryIndex = mockOrders.findIndex(o => o.id === orderId);
            if(deliveryIndex !== -1) {
                mockOrders[deliveryIndex].status = 'delivered';
            }
        }, 3000); 
    }
  }
  
  console.log(`Mock: Updating order ${orderId} status to ${newStatus}`);
  
  return {
    orderId: orderId,
    status: newStatus,
    message: `Status for order ${orderId} updated successfully to ${newStatus}`
  };
};

// Review API - submitReview (MODIFIKASI UNTUK REVIEW PER-PESANAN)
export const submitReview = async (review) => {
  console.log('Mock: Submitting order review', review);
  const orderIndex = mockOrders.findIndex(o => o.id === review.orderId);
  
  const newReview = {
    id: Date.now(),
    orderId: review.orderId,
    rating: review.rating,
    comment: review.comment,
    date: new Date().toLocaleString(),
    customerName: mockOrders[orderIndex]?.customerName || 'Customer'
  };

  if (orderIndex !== -1) {
    // Timpa semua review lama dengan review baru (hanya satu review per order)
    mockOrders[orderIndex].reviews = [newReview]; 
  }

  return newReview;
};
