import React from 'react';
import { Clock, CheckCircle, Package, Truck, Star, Check, CreditCard, XCircle, AlertCircle, Eye } from 'lucide-react';
import { formatCurrency } from '../services/api';

const OrderCard = ({ order, isOwner, onUpdateStatus, onReviewItem, onViewPaymentProof, onVerifyPayment, reviews = [], onConfirmDelivery }) => {
  const getStatusIcon = (status) => {
    switch(status) {
      case 'waiting_payment': return <CreditCard className="w-5 h-5" />;
      case 'payment_uploaded': return <AlertCircle className="w-5 h-5" />;
      case 'pending': return <Clock className="w-5 h-5" />;
      case 'processing': return <Package className="w-5 h-5" />;
      case 'ready': return <CheckCircle className="w-5 h-5" />;
      case 'delivered': return <Truck className="w-5 h-5" />;
      case 'completed': return <Check className="w-5 h-5" />; 
      case 'cancelled': return <XCircle className="w-5 h-5" />;
      default: return <Clock className="w-5 h-5" />;
    }
  };

  const getStatusColor = (status) => {
    switch(status) {
      case 'waiting_payment': return 'bg-purple-100 text-purple-800';
      case 'payment_uploaded': return 'bg-blue-100 text-blue-800';
      case 'pending': return 'bg-yellow-100 text-yellow-800';
      case 'processing': return 'bg-blue-100 text-blue-800';
      case 'ready': return 'bg-green-100 text-green-800';
      case 'delivered': return 'bg-gray-100 text-gray-800';
      case 'completed': return 'bg-green-500 text-white'; 
      case 'cancelled': return 'bg-red-100 text-red-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };
  
  const getStatusText = (status) => {
    switch(status) {
      case 'waiting_payment': return 'Waiting Payment';
      case 'payment_uploaded': return 'Payment Uploaded';
      case 'pending': return 'Pending';
      case 'processing': return 'Processing';
      case 'ready': return 'Ready';
      case 'delivered': return 'Delivered';
      case 'completed': return 'Pesanan Diterima';
      case 'cancelled': return 'Cancelled';
      default: return status;
    }
  };

  // Check if ORDER has been reviewed
  const hasOrderReviewed = () => {
    return reviews && reviews.length > 0;
  };

  // Get ORDER review
  const getOrderReview = () => {
    // Ambil review pertama, karena hanya ada satu per order
    return reviews && reviews.length > 0 ? reviews[0] : null;
  };

  const orderReview = getOrderReview();
  const reviewed = hasOrderReviewed();


  return (
    <div className="bg-white rounded-xl shadow-lg p-6 fade-in">
      <div className="flex justify-between items-start mb-4">
        <div>
          <p className="text-sm text-gray-500">Order #{order.id}</p>
          <p className="text-sm text-gray-600">{order.date}</p>
          {isOwner && (
            <p className="font-semibold text-gray-800 mt-1">Customer: {order.customerName}</p>
          )}
        </div>
        <div className={`flex items-center space-x-2 px-3 py-2 rounded-lg ${getStatusColor(order.status)}`}>
          {getStatusIcon(order.status)}
          <span className="font-semibold capitalize">{getStatusText(order.status)}</span>
        </div>
      </div>

      {/* Payment Info... */}
      {order.paymentMethod && (
        <div className="mb-4 p-4 bg-gray-50 rounded-lg border border-gray-200">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-xs text-gray-600 mb-1">Payment Method:</p>
              <p className="font-semibold text-gray-800">{order.paymentMethod}</p>
            </div>
            {order.paymentProof && (
              <button
                onClick={() => onViewPaymentProof && onViewPaymentProof(order.paymentProof)}
                className="flex items-center space-x-1 px-3 py-1.5 bg-blue-500 text-white text-sm rounded-lg hover:bg-blue-600 transition"
              >
                <Eye className="w-4 h-4" />
                <span>View Proof</span>
              </button>
            )}
          </div>
        </div>
      )}
      
      <div className="border-t pt-4 mb-4 space-y-3">
        {order.items.map(item => {
          return (
            <div key={item.id} className="flex justify-between items-start">
              <div className="flex-1">
                <div className="flex items-center justify-between">
                  <span className="text-gray-700 font-medium">{item.name} x{item.quantity}</span>
                  <span className="text-gray-800 font-medium ml-4">
                    {formatCurrency(item.price * item.quantity)}
                  </span>
                </div>
                {/* LOGIKA REVIEW PER ITEM DIHAPUS */}
              </div>
            </div>
          );
        })}
      </div>
      
      <div className="flex justify-between items-center pt-4 border-t">
        <span className="text-lg font-bold text-gray-800">Total:</span>
        <span className="text-2xl font-bold text-orange-600">
          {formatCurrency(order.total)}
        </span>
      </div>

      {/* --- LOGIKA REVIEW KESELURUHAN PESANAN (BARU/MODIFIKASI) --- */}
      {(order.status === 'completed' || isOwner) && (reviewed || order.status === 'completed') && (
        <div className="mt-4 p-3 bg-gray-50 rounded-lg border border-gray-200">
            <h3 className="text-sm font-semibold text-gray-700 mb-2">Order Review Status:</h3>
            
            {/* Tampilan Customer setelah Diterima */}
            {!isOwner && order.status === 'completed' && (
              <>
                {reviewed ? (
                  <div className="inline-flex items-center space-x-2 px-3 py-1.5 bg-green-100 text-green-700 text-sm rounded-lg">
                    <Check className="w-4 h-4" />
                    <span>Order Reviewed ({orderReview.rating}/5)</span>
                  </div>
                ) : (
                  <button
                    // Hanya passing order ID
                    onClick={() => onReviewItem(order.id)} 
                    className="inline-flex items-center space-x-1 px-3 py-1.5 bg-orange-500 text-white text-sm rounded-lg hover:bg-orange-600 transition"
                  >
                    <Star className="w-4 h-4" />
                    <span>Tulis Review Pesanan</span>
                  </button>
                )}
              </>
            )}

            {/* Display Review for Owner/Details */}
            {reviewed && orderReview && (
              <div className="mt-2 p-3 bg-yellow-50 border border-yellow-200 rounded-lg">
                <div className="flex items-center space-x-2 mb-1">
                  <div className="flex">
                    {[...Array(5)].map((_, i) => (
                      <Star
                        key={i}
                        className={`w-4 h-4 ${
                          i < orderReview.rating
                            ? 'fill-yellow-400 text-yellow-400'
                            : 'text-gray-300'
                        }`}
                      />
                    ))}
                  </div>
                  <span className="text-sm font-semibold text-gray-700">
                    {orderReview.rating}/5 
                    {isOwner && (` - ${orderReview.customerName}`)}
                  </span>
                </div>
                {orderReview.comment && (
                  <p className="text-sm text-gray-700 italic">"{orderReview.comment}"</p>
                )}
              </div>
            )}
        </div>
      )}
      {/* --- AKHIR LOGIKA REVIEW KESELURUHAN --- */}
      
      {/* Owner Actions... (TIDAK BERUBAH) */}
      {isOwner && (
        <div className="mt-4 space-y-2">
          {/* ... */}
        </div>
      )}

      {/* Customer Delivery Confirmation Action (NEW FEATURE) */}
      {!isOwner && order.status === 'delivered' && onConfirmDelivery && (
        <div className="mt-4">
          <button
            onClick={() => onConfirmDelivery(order.id)}
            className="w-full py-2 bg-orange-600 text-white rounded-lg hover:bg-orange-700 transition font-semibold"
          >
            Pesanan Telah Sampai
          </button>
        </div>
      )}
      
      {/* Customer Payment Reminder... (TIDAK BERUBAH) */}

    </div>
  );
};

export default OrderCard;
